import React, { useState, useEffect } from 'react';
import { cobrancasService } from '../../services/cobrancasService';

const CobrancasList = () => {
  const [cobrancas, setCobrancas] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const [marcandoPago, setMarcandoPago] = useState(null);

  useEffect(() => {
    carregarCobrancas();
  }, []);

  const carregarCobrancas = async () => {
    try {
      setLoading(true);
      setError(null);
      const data = await cobrancasService.listar();
      // Garantir que sempre seja um array
      const cobrancasArray = Array.isArray(data) ? data : (data.results || data.data || []);
      setCobrancas(cobrancasArray);
    } catch (err) {
      setError(err.message);
      setCobrancas([]); // Garantir que seja array mesmo em caso de erro
    } finally {
      setLoading(false);
    }
  };

  const handleMarcarComoPago = async (id) => {
    if (window.confirm('Deseja marcar esta cobrança como paga?')) {
      try {
        setMarcandoPago(id);
        setError(null);
        setSuccess(null);
        await cobrancasService.marcarComoPago(id);
        setSuccess('Cobrança marcada como paga com sucesso!');
        carregarCobrancas();
      } catch (err) {
        setError(`Erro ao marcar como pago: ${err.message}`);
      } finally {
        setMarcandoPago(null);
      }
    }
  };

  const formatarMoeda = (valor) => {
    return new Intl.NumberFormat('pt-BR', {
      style: 'currency',
      currency: 'BRL',
    }).format(valor);
  };

  const formatarData = (data) => {
    if (!data) return '-';
    return new Date(data).toLocaleDateString('pt-BR');
  };

  const getStatusClass = (status) => {
    switch (status) {
      case 'PAGO':
        return 'status-pago';
      case 'PENDENTE':
        return 'status-pendente';
      case 'ATRASADO':
        return 'status-atrasado';
      case 'CANCELADO':
        return 'status-cancelado';
      default:
        return '';
    }
  };

  const getStatusLabel = (status) => {
    const labels = {
      PAGO: 'Pago',
      PENDENTE: 'Pendente',
      ATRASADO: 'Atrasado',
      CANCELADO: 'Cancelado',
    };
    return labels[status] || status;
  };

  // Filtrar cobranças por status
  const [filtroStatus, setFiltroStatus] = useState('TODAS');

  const cobrancasFiltradas = Array.isArray(cobrancas) 
    ? (filtroStatus === 'TODAS'
        ? cobrancas
        : cobrancas.filter(c => c.status_cobranca === filtroStatus))
    : [];

  if (loading) {
    return <div className="loading">Carregando cobranças...</div>;
  }

  return (
    <div>
      <div className="page-header">
        <h2>Cobranças</h2>
        <select
          value={filtroStatus}
          onChange={(e) => setFiltroStatus(e.target.value)}
          style={{
            padding: '0.75rem 1rem',
            border: '1px solid #d1d5db',
            borderRadius: '8px',
            fontSize: '1rem',
          }}
        >
          <option value="TODAS">Todas</option>
          <option value="PENDENTE">Pendentes</option>
          <option value="PAGO">Pagas</option>
          <option value="ATRASADO">Atrasadas</option>
          <option value="CANCELADO">Canceladas</option>
        </select>
      </div>

      {error && <div className="error">{error}</div>}
      {success && <div className="success">{success}</div>}

      {!Array.isArray(cobrancasFiltradas) || cobrancasFiltradas.length === 0 ? (
        <div className="empty-state">
          <h3>Nenhuma cobrança encontrada</h3>
          <p>
            {filtroStatus === 'TODAS'
              ? 'Não há cobranças cadastradas.'
              : `Não há cobranças com status "${getStatusLabel(filtroStatus)}".`}
          </p>
        </div>
      ) : (
        <div className="table-container">
          <table>
            <thead>
              <tr>
                <th>Cliente</th>
                <th>Referência</th>
                <th>Vencimento</th>
                <th>Valor Base</th>
                <th>Multa/Juros</th>
                <th>Total</th>
                <th>Pagamento</th>
                <th>Status</th>
                <th>Ações</th>
              </tr>
            </thead>
            <tbody>
              {Array.isArray(cobrancasFiltradas) && cobrancasFiltradas.map((cobranca) => (
                <tr key={cobranca.id}>
                  <td>{cobranca.cliente_nome || '-'}</td>
                  <td>{cobranca.referencia_ciclo || '-'}</td>
                  <td>{formatarData(cobranca.data_vencimento)}</td>
                  <td>{formatarMoeda(cobranca.valor_base)}</td>
                  <td>{formatarMoeda(cobranca.valor_multa_juros || 0)}</td>
                  <td>
                    <strong>{formatarMoeda(cobranca.valor_total_devido)}</strong>
                  </td>
                  <td>{formatarData(cobranca.data_pagamento)}</td>
                  <td>
                    <span className={`status-badge ${getStatusClass(cobranca.status_cobranca)}`}>
                      {getStatusLabel(cobranca.status_cobranca)}
                    </span>
                  </td>
                  <td>
                    {cobranca.status_cobranca === 'PENDENTE' ||
                    cobranca.status_cobranca === 'ATRASADO' ? (
                      <button
                        onClick={() => handleMarcarComoPago(cobranca.id)}
                        className="btn btn-success"
                        disabled={marcandoPago === cobranca.id}
                        style={{ padding: '0.5rem 1rem', fontSize: '0.875rem' }}
                      >
                        {marcandoPago === cobranca.id ? 'Processando...' : 'Marcar Pago'}
                      </button>
                    ) : (
                      '-'
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default CobrancasList;
