import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { clientesService } from '../../services/clientesService';
import { planosService } from '../../services/planosService';

const ClienteForm = () => {
  const navigate = useNavigate();
  const { id } = useParams();
  const isEdit = !!id;

  const [formData, setFormData] = useState({
    plano: '',
    nome: '',
    cpf: '',
    telefone_whatsapp: '',
    email: '',
    data_inicio_contrato: '',
    status_cliente: 'ATIVO',
  });

  const [planos, setPlanos] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [errorPlanos, setErrorPlanos] = useState(null);
  const [loadingPlanos, setLoadingPlanos] = useState(true);

  useEffect(() => {
    carregarPlanos();
    if (isEdit) {
      carregarCliente();
    }
  }, [id]);

  const carregarPlanos = async () => {
    try {
      setErrorPlanos(null);
      const data = await planosService.listar();
      // Garantir que sempre seja um array
      const planosArray = Array.isArray(data) ? data : (data.results || data.data || []);
      setPlanos(planosArray);
    } catch (err) {
      setErrorPlanos(`Erro ao carregar planos: ${err.message}`);
      setPlanos([]); // Garantir que seja array mesmo em caso de erro
      console.error('Erro ao carregar planos:', err);
    } finally {
      setLoadingPlanos(false);
    }
  };

  const carregarCliente = async () => {
    try {
      setLoading(true);
      const cliente = await clientesService.buscarPorId(id);
      setFormData({
        plano: cliente.plano,
        nome: cliente.nome,
        cpf: cliente.cpf,
        telefone_whatsapp: cliente.telefone_whatsapp,
        email: cliente.email,
        data_inicio_contrato: cliente.data_inicio_contrato,
        status_cliente: cliente.status_cliente,
      });
    } catch (err) {
      setError(`Erro ao carregar cliente: ${err.message}`);
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    try {
      if (isEdit) {
        await clientesService.atualizar(id, formData);
        alert('Cliente atualizado com sucesso!');
      } else {
        await clientesService.criar(formData);
        alert('Cliente criado com sucesso!');
      }
      navigate('/clientes');
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  if (loadingPlanos) {
    return <div className="loading">Carregando planos...</div>;
  }

  return (
    <div>
      <div className="page-header">
        <h2>{isEdit ? 'Editar Cliente' : 'Novo Cliente'}</h2>
        <button
          onClick={() => navigate('/clientes')}
          className="btn btn-secondary"
        >
          Voltar
        </button>
      </div>

      {error && <div className="error">{error}</div>}
      {errorPlanos && <div className="error">{errorPlanos}</div>}

      <div className="card">
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="plano">Plano *</label>
            <select
              id="plano"
              name="plano"
              value={formData.plano}
              onChange={handleChange}
              required
              disabled={loadingPlanos || (Array.isArray(planos) && planos.length === 0 && !errorPlanos)}
            >
              <option value="">
                {loadingPlanos 
                  ? 'Carregando planos...' 
                  : (Array.isArray(planos) && planos.length === 0 && !errorPlanos)
                    ? 'Nenhum plano disponível'
                    : 'Selecione um plano'}
              </option>
              {Array.isArray(planos) && planos.map((plano) => (
                <option key={plano.id} value={plano.id}>
                  {plano.nome_plano} - R$ {plano.valor_base?.toFixed(2) || '0.00'}/mês
                </option>
              ))}
            </select>
            {errorPlanos && (
              <small style={{ color: '#ef4444', fontSize: '0.875rem', display: 'block', marginTop: '0.5rem' }}>
                Verifique se o backend está rodando e se o endpoint /api/planos/ está acessível.
              </small>
            )}
          </div>

          <div className="form-group">
            <label htmlFor="nome">Nome Completo *</label>
            <input
              type="text"
              id="nome"
              name="nome"
              value={formData.nome}
              onChange={handleChange}
              required
              placeholder="Ex: João Silva"
            />
          </div>

          <div className="form-group">
            <label htmlFor="cpf">CPF *</label>
            <input
              type="text"
              id="cpf"
              name="cpf"
              value={formData.cpf}
              onChange={handleChange}
              required
              placeholder="12345678901"
              maxLength="11"
            />
          </div>

          <div className="form-group">
            <label htmlFor="telefone_whatsapp">Telefone WhatsApp *</label>
            <input
              type="text"
              id="telefone_whatsapp"
              name="telefone_whatsapp"
              value={formData.telefone_whatsapp}
              onChange={handleChange}
              required
              placeholder="5521999999999"
            />
            <small style={{ color: '#6b7280', fontSize: '0.875rem' }}>
              Formato: código do país + DDD + número (ex: 5521999999999)
            </small>
          </div>

          <div className="form-group">
            <label htmlFor="email">E-mail *</label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              required
              placeholder="joao@example.com"
            />
          </div>

          <div className="form-group">
            <label htmlFor="data_inicio_contrato">Data de Início do Contrato *</label>
            <input
              type="date"
              id="data_inicio_contrato"
              name="data_inicio_contrato"
              value={formData.data_inicio_contrato}
              onChange={handleChange}
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="status_cliente">Status *</label>
            <select
              id="status_cliente"
              name="status_cliente"
              value={formData.status_cliente}
              onChange={handleChange}
              required
            >
              <option value="ATIVO">Ativo</option>
              <option value="INATIVO_ATRASO">Inativo por Atraso</option>
              <option value="INATIVO_MANUAL">Inativo Manual</option>
            </select>
          </div>

          <div className="form-actions">
            <button
              type="submit"
              className="btn btn-primary"
              disabled={loading}
            >
              {loading ? 'Salvando...' : isEdit ? 'Atualizar' : 'Criar'}
            </button>
            <button
              type="button"
              onClick={() => navigate('/clientes')}
              className="btn btn-secondary"
              disabled={loading}
            >
              Cancelar
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default ClienteForm;
